<?phpnamespace App\Http\Middleware;use Closure;use Illuminate\Support\Facades\Auth;class Role{    public function handle($request, Closure $next, $guard = null)    {        // Perform Action        if (Auth::guard($guard)->guest()) {            if ($request->ajax()) {                return response('Unauthorized.', 401);            } else {                return redirect()->guest('/login')->with('access', 'To get access please logged in here!');            }        }        if (Auth::user()->role == 1) {            return $next($request);        } else {            return redirect()->to('/')->with('error', 'you are not authorized for this action !');        }    }}