<?phpnamespace App\Http\Middleware;use Closure;use Illuminate\Support\Facades\Auth;class userAuth{    /**     * Handle an incoming request.     *     * @param  \Illuminate\Http\Request  $request     * @param  \Closure  $next     * @param  string|null  $guard     * @return mixed     */    public function handle($request, Closure $next, $guard = null)    {        if($request->session()->get('userLogin') == 0 || empty($request->session()->get('userLogin'))){            dd('here');			if($request->ajax() || $request->wantsJson()){			    return response('Unauthorized.', 401);            }			else			{				// here code to intiate user login popup will be written			    return redirect()->to('login')->with('error','Please login first to get access!');			}		}        return $next($request);    }}